mapper.select:
SELECT badge_id, name, image, sido_code, gugun_code, type
FROM badges 
WHERE sido_code = #{sidoCode} 
  AND gugun_code = #{gugunCode}

SELECT COUNT(*) 
FROM user_badges
WHERE user_id = #{userId} AND badge_id = #{badgeId}

SELECT COUNT(*) 
FROM guguns 
WHERE sido_code = #{sidoCode}

SELECT COUNT(DISTINCT b.gugun_code)
FROM user_badges ub
JOIN badges b ON ub.badge_id = b.badge_id
WHERE ub.user_id = #{userId} 
  AND b.sido_code = #{sidoCode} 
  AND b.type = 'SI'
